import React from 'react';
import {connect} from 'react-redux';
import {bindActionCreators} from 'redux';
import {fetchUser} from '../../actions';
import axios from 'axios';

import {compose} from "../../utils";
import {withBookstoreService} from "../hoc";
// import LoadingSpinner from "../spinner";
// import ErrorBoundry from "../error-boundry";
import '../news/news.css'

class User extends React.Component {
  state = {
    email: '',
    password: ''
  }
  
  add = (e) => {
    e.preventDefault()
    axios.post('http://localhost:5000/auth/add', this.state)
      .then((res) => {
        console.log(res)
      })
      .catch((err) => console.log(err))
  }
  
  
  render() {
    console.log(this.props.products)
    return (
      <div>
        <h1>Home</h1>
        <button onClick={this.props.fetchUser}>click</button>
  
  
        <body data-gr-c-s-loaded="true">
        <form className="form-signin">
          <div className="text-center mb-4">
            <img className="mb-4"
                 src="/docs/4.5/assets/brand/bootstrap-solid.svg" alt=""
                 width="72" height="72"/>
              <h1 className="h3 mb-3 font-weight-normal">Floating labels</h1>
              <p>Build form controls with floating labels via
                the <code>:placeholder-shown</code> pseudo-element. <a
                  href="https://caniuse.com/#feat=css-placeholder-shown">Works
                  in latest Chrome, Safari, and Firefox.</a></p>
          </div>
    
          <div className="form-label-group">
            <input type="email" id="inputEmail" className="form-control"
                   placeholder="Email address" required="" autoFocus=""/>
              <label htmlFor="inputEmail">Email address</label>
          </div>
    
          <div className="form-label-group">
            <input type="password" id="inputPassword" className="form-control"
                   placeholder="Password" required=""/>
              <label htmlFor="inputPassword">Password</label>
          </div>
    
          <div className="checkbox mb-3">
            <label>
              <input type="checkbox" value="remember-me"/> Remember me
            </label>
          </div>
          <button className="btn btn-lg btn-primary btn-block"
                  type="submit">Sign in
          </button>
          <p className="mt-5 mb-3 text-muted text-center">Â© 2017-2020</p>
        </form>
  
  
        </body>
      </div>
    
    )
  }
}


const mapStateToProps = ({userList: {products, pending, error}}) => {
  return {products, pending, error};
};


const mapDispatchToProps = dispatch => bindActionCreators({
  
  
  fetchUser: fetchUser
  
}, dispatch)


export default compose(withBookstoreService(), connect(mapStateToProps, mapDispatchToProps))(User);
